import { useState } from "react";

const Prices = (props) => {
  const coinDataArray = Object.entries(props.bpi.bpi);

  const onDropdownChange = (num) => {
    return {
      currency: coinDataArray[num][1].code,
      rate: coinDataArray[num][1].rate,
    };
  };

  const [coinData, setCoinData] = useState({
    currency: "USD",
    rate: "not checked",
  });

  return (
    <div>
      <select
        onChange={(selected) =>
          setCoinData(onDropdownChange(selected.target.value))
        }
      >
        <option value="0">US Dollars</option>
        <option value="1">GB Pounds</option>
        <option value="2">Euros</option>
      </select>
      <ul>
        <li>{`Bitcoin rate for ${coinData.currency} : ${coinData.rate}`}</li>
      </ul>
    </div>
  );
};

export default Prices;
