// import { useState } from "react";

const Prices = (props) => {
  //   const [coinData] = useState({
  //     currency: "USD",
  //   });

  const usableCoinData = Object.entries(props.bpi.bpi);

  return (
    <div>
      <select id="currencies">
        <option value="0">USD</option>
        <option value="1">GBP</option>
        <option value="2">EUR</option>
      </select>
      <ul>
        <li>
          {`Bitcoin rate for ${usableCoinData[0][1].code} : ${usableCoinData[0][1].rate}`}
        </li>
      </ul>
    </div>
  );
};

export default Prices;
