import { useState } from "react";

const Prices = (props) => {
  const [coinData, setCoinData] = useState({
    currency: "USD",
    rate: "",
  });

  const usableCoinData = Object.entries(props.bpi.bpi);
  console.log("usableCoinData", usableCoinData);

  return (
    <div>
      <select
        onChange={(selected) =>
          setCoinData({ currency: selected.target.value })
        }
      >
        <option value="USD">USD</option>
        <option value="GBP">GBP</option>
        <option value="EUR">EUR</option>
      </select>
      {console.log(coinData)}
      <ul>
        <li>
          {`Bitcoin rate for ${coinData.currency} : ${usableCoinData[0][1].rate}`}
        </li>
      </ul>
    </div>
  );
};

export default Prices;
