import { useState } from "react";

const Prices = (props) => {
  const [coinData, setCoinData] = useState({
    currency: "USD",
    rate: "not checked",
  });

  const usableCoinData = Object.entries(props.bpi.bpi);
  console.log("usableCoinData", usableCoinData);

  const onDropdownChange = (num) => {
    return {
      currency: usableCoinData[num][1].code,
      rate: usableCoinData[num][1].rate,
    };
  };

  return (
    <div>
      <select
        onChange={(selected) =>
          setCoinData(onDropdownChange(selected.target.value))
        }
      >
        <option value="0">USD</option>
        <option value="1">GBP</option>
        <option value="2">EUR</option>
      </select>
      <ul>
        <li>
          {`Bitcoin rate for ${coinData.currency} : ${usableCoinData[0][1].rate}`}
        </li>
      </ul>
    </div>
  );
};

export default Prices;
